
public class AddSeq {
	public static void main(String args[])
	{
		System.out.println("Hi there let's start programming today");
		System.out.println("The addition is " + getAdditionTill(10));
		
	}

	public static int getAdditionTill(int t) {
		// TODO Auto-generated method stub
		
		return (t*(t+1))/2;
	}
}
